/* eslint-disable no-unused-vars */

const leadingComment = `
/* eslint-disable deprecation/deprecation */

/* THIS IS AN AUTOGENERATED FILE. DO NOT EDIT THIS FILE DIRECTLY. */
`

const deprecatedComment = `/** @deprecated */

`

function templateFn({ template }, opts, { imports, componentName, props, jsx, exports }) {
  const code = `
  LEADING_COMMENT
  import React, { forwardRef, SVGProps, Ref } from 'react'
  DEPRECATED_COMMENT
  const COMPONENT_NAME = forwardRef((props: SVGProps<SVGSVGElement>, svgRef: Ref<SVGSVGElement>) => JSX)
  export default COMPONENT_NAME;
  `
  const tpl = template.smart(code, {
    plugins: ['typescript'],
    preserveComments: true,
  })

  return tpl({
    COMPONENT_NAME: componentName,
    JSX: jsx,
    LEADING_COMMENT: leadingComment,
    DEPRECATED_COMMENT: deprecatedComment,
  })
}

module.exports = templateFn
